import React from 'react';
import {connect} from 'react-redux'
import {Col, Row} from 'reactstrap'
import styled from 'styled-components'
import LoginForm from '../component/LoginForm';
import {loadListGeo, loginAction,loginOAuth2} from "../app/store/actions";
import {Redirect} from "react-router-dom";
import Map from "../component/map";
import InfoWindow from "../component/map/infoWindow";
import GoogleMap from 'google-distance-matrix';
import Button from "./../component/button";
import queryString from 'query-string'
import * as storage from '../app/storage'
import get from "lodash/get";
import isString from "lodash/isString";
import IcLogin from "../component/images/login";
import store from '../app/store'

const initialState = {
  user: storage.get('session_user'),
  authenticated: false
}
const assets = {
  main: "https://images.pexels.com/photos/938965/pexels-photo-938965.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260",
  logo: require('../assets/img/image.png'),
  map: require('../assets/img/map.png'),
}
const delay = (ms) => new Promise(resolve =>
  setTimeout(resolve, ms)
);
class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mapActive: false,
      data:null,
      markers: []
    }
    this.onClick = this.displayMap.bind(this);
  }

  componentDidMount() {
    let params = queryString.parse(this.props.location.search)
    if(Object.getOwnPropertyNames(params).length !== 0){
      const user = {
        token: params.token
      }
      storage.set('session_user', user);
      this.props.dispatch(loginOAuth2()).then(()=>this.props.history.push('/'))
    }

  }
  displayMap() {
    this.props.dispatch(loadListGeo()).then((data) => {
      this.setState({markers: data.payload.dabs, mapActive: true})
    })
  }

  render() {
    return (
      <LoginStyle>
        <Container-fluid>
          <Row>
            <Col md={9}>
              {this.state.mapActive &&
              <Map
                googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyD9Y-Gp7gMPSZrcWciBoGzaLaCVYYr4YrY&libraries=places&libraries=geometry"
                loadingElement={<div style={{height: `100%`}}/>}
                containerElement={<div style={{height: `900px`}}/>}
                mapElement={<div style={{height: `100%`}}/>}
                markers={this.state.markers}
                infoWindow={(marker) => (
                  <span>
                    <InfoWindow titleInfoWin={<span>Adresse : {marker.agencyId}</span>}
                                headers={[
                                  {name: 'type', title: 'Type :'},
                                  {name: 'name', title: 'Nom :'},
                                  {name: 'address.distance', title: 'Distance :'},
                                  {name: 'address.phoneNumber', title: 'Numéro de téléphone :'},
                                  {name: 'address.streetAddress', title: 'Adresse :'},
                                  {name: 'services', title: 'Service :'},
                                  {name: 'status', title: 'Status :'}
                                ]}
                                content={marker}></InfoWindow>
                  </span>
                )}
                zoom={14}
                defaultCenter={{lat: parseFloat(33.5731104), lng: parseFloat(-7.589843400000063)}}
                icon={(item) => (
                  null
                )}
                distance={3}
              />
              }
              {
                !this.state.mapActive && <img src={assets.main} className="ui-hero"/>
              }
            </Col>
            <Col>
              <div className="ui-form">
                <img style={{width:'40px'}} src={assets.logo} className="ui-logo mb-5"/> <span>UNIBANK</span>
                <LoginForm
                  onSubmit={(form) => this.props.dispatch(loginAction(form)).then(() => this.props.history.push('/'))}/>
                <span className="ui-button map " onClick={this.onClick}>Localiser <img src={assets.map}className="ui-hero"/></span>

                <div className="mt-3 text-muted">
                  <small>
                    &copy; 2018 Société Générale ABS
                  </small>
                </div>
              </div>
            </Col>
          </Row>
        </Container-fluid>
      </LoginStyle>
    )
  }
}

const mapState2Props = (state) => ({session: state.session})
export default connect(mapState2Props)(Login);
const LoginStyle = styled.div`

  .ui-logo {
    margin-top: 20px;
    width: 60%;
  }


  @media (min-width: 768px) {

    .ui-button{
    cursor: pointer;
    vertical-align: middle;

    }
    .ui-form {
    padding-right:30px
      padding-top: 180px;
    }
   .col .img{
  float:right
  margin-right:10px
  margin-top:10px;
  width:40px;
  height:40px;
  padding:7px;
      background: #000;
      border-radius: 30px;
      border: 2px solid #000;
      transition: all 0.8s;
      svg {
        transition: all 0.8s;
          fill: #fff;
      }
    .ui-logo {
      width: 75%;
    }

  }
`
